securities.map:
function(doc) {
      if(!doc.blank){
            var date = (doc.decl_date) ? doc.decl_date : "";
                if(doc.securities.length > 0){
                            doc.securities.forEach(function(i){
                                            emit([doc.decl_id,doc.scrape_date,doc.name,date], [i.name,i.issuer,i.type,i.price,i.quantity]);
                            });
                }
      }
}

